import React, { Component } from 'react';
import { Button, Tooltip } from 'antd';
import { SearchOutlined } from '@ant-design/icons';

class Group extends Component {
    state = { 
        show: false
     }

    toggle = () => this.setState((currentState) => ({show: !currentState.show}));

    render() { 
        return (
            <div>
            <Tooltip title="search">
            <Button type="primary" shape="circle" icon={<SearchOutlined />} onclick="showInput()" />
            </Tooltip>

            <div>
			  <button onClick={this.toggle}>toggle: {this.state.show ? 'show' : 'hide'}</button>    
                {this.state.show && <div>Hi there</div>}
		  </div>

              <h2 style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                Welcome to Grupid !
                     </h2>
            </div>
        );
    }
}

ReactDOM.render(<Toggle />, document.getElementById('search'));

export default Group;
